import React, { useState } from 'react';
import { StyleSheet , View } from 'react-native';
import LabelledInput from './LabelledInput';

export default function Form(props) {
    const computeCardNumber = (str) => {
        // Utilise une expression régulière pour conserver uniquement les chiffres
        const digitsOnly = str.replace(/\D/g, '');

        let formattedStr = '';
        for (let i = 0; i < digitsOnly.length; i++) {
            formattedStr += digitsOnly[i];
            if ((i + 1) % 4 === 0 && i < digitsOnly.length - 1) {
                formattedStr += ' ';
            }
        }
        if(formattedStr.length > 19) {
            formattedStr = formattedStr.substring(0, 20);
        }
        return formattedStr;
    }


    return (
        <View style={styles.main}>
            <LabelledInput computeText={computeCardNumber} text='Card Number' placeholder='XXXX-XXXX-XXXX-XXXX'/>
            <LabelledInput computeText={() => {}} text=''/>
        </View>
    )
}

const styles = StyleSheet.create({
    main: {
        width: '80%',
        height: '50%',
        backgroundColor: 'white',
        borderRadius: 10,
        shadowColor: '#171717',
        shadowOffset: {width: 0, height: 6},
        shadowOpacity: 0.2,
        shadowRadius: 10,
    },
});
